import React, {useState} from 'react';

import {v4  as uuidv4} from 'uuid';
//componentes
import TablaUno from '../Tablas/Tablauno.jsx';
import Registrarse from '../Formularios/Resgistrarse';
import EditarUsuarios from '../Formularios/EditarUsuarios';
const TablaUsuario = () => {

    const datos = [
        { id: uuidv4(), usuario:'farius-red', nombre:'daniel', rol: 'fan', telefono: '3186956700', correo:'aaaa@hola' },

     ]

   const [usuarios, setUsuarios] = useState(datos);

   //agregar usuarios

   const adduser =(user )=>{
    user.id= uuidv4();

       setUsuarios([
          ...usuarios,
          user
       ])
    }
     // eliminar usuario 

     const eliminarUsuarios=(id)=>{
        setUsuarios(usuarios.filter(user => user.id !== id))
     }

     //editar usuarios
      const [Edit, setEdit] = useState(false);

    return ( 
        <div className="container d-flex flex-row">

         <div className="row">

             <div className="col">
                 <h2>Ver Usuarios</h2>
                 <div className="d-block">
                  <TablaUno
                   usuarios={usuarios} 
                   eliminarUsuarios={eliminarUsuarios} 
                   Edit={Edit}
                    adduser={adduser}
                    />
                 </div>
             </div>
            </div>
        </div>
     );
}

export default TablaUsuario;
