import React, {useState} from 'react';
import TablaUno from '/home/daniel/Documentos/Paginas/juego/src/componentes/Tablas/Tablauno.jsx';
import Registrarse from '../Formularios/Resgistrarse';

const TablaUsuario = () => {

    const datos = [
        { id:1, nombre:'daniel', rol:'Reclutador'},
        { id:2, nombre: 'angel', rol: 'aprendiz'},
        { id:3,  nombre:'dian', rol:'profesor'},
     ]

   const [Data, setData] = useState(datos)

   //agregar datos

   const adduser =(user)=>{
    user.id = user+1;
       setData([
          ...user,
          user
       ])


   }
    return ( 
        <div className="container d-flex flex-row">

         <div className="row">
             <div className="col">

                 <h2>ADD user</h2>
                <div className="d-row">
                  <Registrarse adduser={adduser}/>
                </div>
             </div>
             <div className="col">
                 <h2>Ver Usuarios</h2>
                 <div className="d-block">
                  <TablaUno usuarios={Data}/>
                 </div>
             </div>
            </div>
        </div>
     );
}

export default TablaUsuario;
